import React, { useState } from "react";
import Chart from "react-apexcharts";

const Radial = (props) => {
	const [options] = useState({
		chart: {
			height: 350,
			type: 'radialBar',
		  },
		  plotOptions: {
			radialBar: {
			  hollow: {
				margin: 15,
				size: '70%',
				image: '../../assets/images/clock.png',
				imageWidth: 64,
				imageHeight: 64,
				imageClipped: false
			  },
			  dataLabels: {
				name: {
				  show: false,
				  color: '#fff'
				},
				value: {
				  show: true,
				  color: '#333',
				  offsetY: 70,
				  fontSize: '22px'
				}
			  }
			}
		  },
		  fill: {
			type: 'image',
			image: {
			  src: ['../../assets/images/4274635880_809a4b9d0d_z.jpg'],
			}
		  },
		  stroke: {
			lineCap: 'round'
		  },
		  labels: ['Volatility']
	});

	const [series] = useState(props && props.series ? props.series : []);

	
	let radialFrag = (
		<div className="col-md-6">
			<div className="mt-col" style={{ maxWidth: "100%" }}>
				<div className="row">
					<div className="card pt-0">
						<Chart options={options} series={series} />
					</div>
				</div>
			</div>
		</div>
	);

	return <React.Fragment>{radialFrag}</React.Fragment>;
};

export default Radial;
