import React, { useState } from "react";
import Chart from "react-apexcharts";

class Radial1 extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
		
		  series: props.series,
		  options: {
			chart: {
			  height: 500,
			  type: 'radialBar',
			  toolbar: {
				show: true
			  }
			},
			plotOptions: {
			  radialBar: {
				startAngle: -135,
				endAngle: 225,
				 hollow: {
				  margin: 0,
				  size: '70%',
				  background: '#fff',
				  image: undefined,
				  imageOffsetX: 0,
				  imageOffsetY: 0,
				  position: 'front',
				  dropShadow: {
					enabled: true,
					top: 3,
					left: 0,
					blur: 4,
					opacity: 0.24
				  }
				},
				track: {
				  background: '#fff',
				  strokeWidth: '67%',
				  margin: 0, // margin is in pixels
				  dropShadow: {
					enabled: true,
					top: -3,
					left: 0,
					blur: 4,
					opacity: 0.35
				  }
				},
			
				dataLabels: {
				  show: true,
				  name: {
					offsetY: -10,
					show: true,
					color: '#888',
					fontSize: '17px'
				  },
				  value: {
					formatter: function(val) {
					  return parseInt(val);
					},
					color: '#111',
					fontSize: '36px',
					show: true,
				  }
				}
			  }
			},
			fill: {
			  type: 'gradient',
			  gradient: {
				shade: 'dark',
				type: 'horizontal',
				shadeIntensity: 0.5,
				gradientToColors: ['#ABE5A1'],
				inverseColors: true,
				opacityFrom: 1,
				opacityTo: 1,
				stops: [0, 100]
			  }
			},
			stroke: {
			  lineCap: 'round'
			},
			labels: ['Percent'],
		  },
		
		
		};
	  }

	

	  render() {
		return (
		  

	<div id="card">
<div id="chart">
<Chart options={this.state.options} series={this.state.series} type="radialBar" height={500} />
</div>
</div>
  

		);
	  }
	}

export default Radial1;
