import React, { useState } from "react";
import Chart from "react-apexcharts";

class RadialStroked extends React.Component {
	constructor(props) {
		super(props);

		this.state = {
			series: props.series,
			options: {
				chart: {
					height: 500,
					type: 'radialBar',
					offsetY: -10
				},
				plotOptions: {
					radialBar: {
						startAngle: -135,
						endAngle: 135,
						hollow: {
							margin: 1,
							size: "50%",
							background: "gray"
						},
						dataLabels: {
							name: {
								fontSize: '16px',
								color: undefined,
								offsetY: 120
							},
							value: {
								offsetY: -10, // 76,
								fontSize: '30px',
								color: undefined,
								formatter: function (val) {
									// return val + '' + props.unit;
									return val;
								}
							}
						}
					}
				},
				fill: {
					type: 'gradient',
					gradient: {
							shade: 'dark',
							shadeIntensity: 0.15,
							inverseColors: false,
							opacityFrom: 1,
							opacityTo: 1,
							stops: [0, 50, 65, 91]
					},
				},
				stroke: {
					dashArray: 4
				},
				labels: props.label,
			},
		
		
		};
	}

	  render() {
		return (
			<div id="card">
				<div id="chart">
					<Chart options={this.state.options} series={this.state.series} type="radialBar" height={250} />
				</div>
			</div>
		);
	  }
	}

export default RadialStroked;
