import React, { PureComponent } from 'react';
import { withRouter, Link } from 'react-router-dom';

import Radial from "../../Chart/Radial";
import RadialGradient from "../../Chart/RadialGradient";
import RadialSemiCircle from "../../Chart/RadialSemiCircle";
import RadialStroked from "../../Chart/RadialStroked";
import { CountdownCircleTimer } from 'react-countdown-circle-timer'

type State = {

}

type Props = {
    dashboardState : Object,
    fetchDashboard: Function,
    resetDashboard: Function
}
// let timer = 60;

let isLoading = false;




class DashboardContainer extends PureComponent<Props, State> {

  constructor(props: Props, context: Context) {
    super(props, context);
    this.state = {
     }
    this.timeMsg = 'Until refresh';
    this.timer = 10;
  }


  componentWillMount() {
    console.log('componentWillMount');
    // const {fetchDashboard, resetDashboard } = this.props;
    setTimeout(() => {
      console.log('componentWillMount refreshing ...');
			this.props.fetchDashboard('all');
		}, 1000);
  }

  componentDidMount() {
    console.log('componentDidMount', this.timer);
    this.timer = setInterval(() => {
      this.isLoading = true;
      console.log('componentDidMount refreshing ...');
      this.props.fetchDashboard('all');
    },(10000))
  }

  componentWillUnmount() {
    clearInterval(this.timer);
  }

  render() {
    const { fetchDashboard, resetDashboard, dashboardState } = this.props;
    console.log('dashboardState', dashboardState)
    if (dashboardState && dashboardState.status === 'Succeed') {
      this.isLoading = false;
    } 

    let timerMessage = 'Until refresh';

    const renderTime = ({ remainingTime }) => {
      if (remainingTime === 1) {
        this.timeMsg = 'Refreshing';
        return <div className="timer" style={{color: 'yellow', 'font-size': '10px'}}>0</div>;
      } else {
        timerMessage = 'Until refresh';
      }

      return (
        <div className="timer">
          <div className="value" style={{color: 'yellow', 'font-size': '10px'}}>{remainingTime}</div>
          {/* <div className="text">Refresh in&nbsp;{remainingTime}&nbsp;seconds</div>
          <div className="value">{remainingTime}</div>
          <div className="text">seconds</div>  */}
        </div>
      );
    };

    return (

        <React.Fragment>
            {/* <div style={{'margin-left': '50px'}}>
                <p style={{color: 'white'}}>Reading @ Oct 23, 2021 10:30AM</p>
            </div> */}

            <br/>
            <div style={{ display: this.isLoading ? "none" : "block" }}>
            <div style={{'margin-left': '50px', display: 'flex'}}>
                <CountdownCircleTimer
                  isPlaying
                  duration={this.timer}
                  colors={[["#004777", 0.33], ["#F7B801", 0.33], ["#A30000"]]}
                  size={30}
                  strokeWidth={3}
                  onComplete={() => [true, 1000]}
                >
                  {renderTime}
                </CountdownCircleTimer>
                <span style={{color: 'white', 'font-size': '14px'}}>&nbsp;&nbsp;{this.timeMsg}</span>
                <span style={{color: 'white', 'font-size': '14px'}}>&nbsp;&nbsp;&nbsp;Data shown are collected recorded on Oct 23, 2021 10:30AM</span>
              </div>
              <br/>
              <div className="mt-inner">

                   <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[82]}
                        label={['Temperature', '2021-10-24 5:00 AM']}
                        unit={'Farenheiht'}
                      />
                      </div>
                    </div>
                  </div>

                   <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[80]}
                        label={['Pressure (psi)']}
                        unit={'psi'}
                      />
                      </div>
                    </div>
                  </div>


                   <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[1013]}
                        label={['Altitude (mbar)']}
                        unit={'mbar'}
                      />
                      </div>
                    </div>
                  </div>

                   <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[38.3]}
                        label={['Humidity']}
                        unit={''}
                      />
                      </div>
                    </div>
                  </div>

                  <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[10]}
                        label={['Soil Moisture (%)']}
                        unit={'percent'}
                      />
                      </div>
                    </div>
                  </div>

                  <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[1500]}
                        label={['Light Intensity (lux)']}
                        unit={'lux'}
                      />
                      </div>
                    </div>
                  </div>

                  <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[12]}
                        label={['Flow Meter (per min)']}
                        unit={'per min'}
                      />
                      </div>
                    </div>
                  </div>

                   <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[24]}
                        label={['Nitrogen (mg/dL)']}
                        unit={'mg/dL'}
                      />
                      </div>
                    </div>
                  </div>

                   <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[4.5]}
                        label={['Phosphorous (mg/dL)']}
                        unit={'mg/dL'}
                      />
                      </div>
                    </div>
                  </div>

                  <div class="div-dash">
                    <div class="row rptpad div-dash-head">
                      <div class="row st-no-pad-no-margin">
                      <RadialStroked
                        series={[103]}
                        label={['Potassium (ppm)']}
                        unit={'ppm'}
                      />
                      </div>
                    </div>
                  </div>
              </div>
            </div>
        </React.Fragment>
    );
  }
}

export default withRouter(DashboardContainer);
